import './App.css';
import Navigation from './components/Navigation';
import Slideshow from './components/Slideshow';
import {Image, FormControl} from 'react-bootstrap';
import BookCard from './components/BookCard'
import FooterPage from './components/FooterPage';
import React, { Component } from 'react'

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null,
    };
  }
  srchFunc = (e) => {
    const searchh = e.target.elements.srchBar.value
      e.preventDefault()
      fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchh}:keyes&key=AIzaSyAaCiGPlCZcM6BcH4laKHqE0OWezL-us7g`)
        .then(response => response.json())
        .then(data => this.setState({ data }));
    }

  render() {
    return (
      <div>
        <Navigation/>
        <Image className="bgi" src="https://www.qualtrics.com/m/assets/blog/wp-content/uploads/2018/08/shutterstock_1068141515.jpg"flued />
        <div className="searchBAR">
       <form onSubmit={this.srchFunc}>
        <FormControl type="text" name="srchBar" placeholder="Search" className="mr-sm-4" />
        <button type="submit">Submit</button>
       </form>
       </div>
       {this.state.data !== null && this.state.data.items.map((bookss,i)=>{
          <BookCard book={bookss}/>
          // console.log(bookss)
       })}

        <FooterPage/>
      </div>
    )
  }
}
